/* eslint-disable react/jsx-no-bind */
/* eslint-disable react/jsx-filename-extension */
import {
  StyleSheet, Text, View, Button,
} from 'react-native';

import {
  React, useState, useRef, useEffect,
} from 'react';
import Questions from './components/Questions';
import Results from './components/Results';
// import questionsList from './assets/questions.json';
import { getQuestions } from './modules/api';
import Login from './components/Login';
import Feedback from './components/Feedback';
import Storage from './modules/Storage';

const styles = StyleSheet.create({
  title: {
    marginTop: 100,
    alignItems: 'center',
  },
  blueRectangle: {
    backgroundColor: 'rgb(0,174,255)',
    padding: 10,
    marginBottom: 20,
    borderRadius: 20,
    height: 120,
    width: 350,
    alignItems: 'center',
    justifyContent: 'center',
  },
  titleText: {
    fontSize: 30,
    fontWeight: 'bold',
  },
  loginBox: {
    marginTop: 40,
    alignItems: 'center',
  },
  cyanRectangle: {
    backgroundColor: 'cyan',
    borderRadius: 20,
    width: 300,
    height: 300,
    marginBottom: 30,
    marginTop: 80,
    alignItems: 'center',
  },
  whiteRectangle: {
    position: 'relative',
    top: 40,
    backgroundColor: 'white',
    borderRadius: 20,
    width: 220,
    height: 220,
    alignItems: 'center',
    textAlign: 'center',
  },
  boldInfo: {
    fontWeight: 'bold',
    fontSize: 16,
  },
  info: {
    fontWeight: 'normal',
  },
  infoContainer: {
    marginTop: 40,
    alignItems: 'center',
    marginLeft: 10,
  },
  deleteButtonContainer: {
    width: 100,
  },
  infoDeleteContainer: {
    flexDirection: 'row',
    marginTop: 20,
    alignItems: 'center',
    justifyContent: 'center',
  },
});

function App() {
  const name = useRef('');
  const bestScore = useRef(0);
  const [error, setError] = useState('');
  const [nextPage, setNext] = useState('question');
  const [score, setScore] = useState();
  const bestUser = useRef('');
  const bestOverall = useRef('');
  const questionsList = useRef([]);
  // const [restart, setRestart] = useState(false);
  async function initializeBestUser() {
    const startingBest = await Storage.getBestUser();
    const startingBestUser = startingBest.user;
    const startingBestScore = startingBest.best;
    bestUser.current = startingBestUser;
    bestOverall.current = startingBestScore;
  }
  async function fetchQuestions() {
    questionsList.current = await getQuestions();
  }

  useEffect(() => {
    // RestartGame();
    initializeBestUser();
    fetchQuestions();
  }, []);

  const start = useRef(false);
  const counter = useRef(0);

  const topPlayers = useRef();

  const [correct, setCorrect] = useState(false);

  async function LoginUser(info) {
    try {
      const RegEx = /^[a-zA-Z0-9]+$/;
      if (RegEx.test(info.name)) {
        name.current = info.name;
        if (await Storage.existingUser(name.current)) {
          bestScore.current = await Storage.getBestScore(name.current);
        } else {
          await Storage.addUser(name.current, 0);
        }
        start.current = true;
        setScore(0);
      } else {
        setError('Use only letters and numbers');
      }
    } catch (err) {
      throw new Error('login error');
    }
  }

  async function RestartGame() {
    try {
      setScore();
      setError('');
      setNext('question');
      name.current = '';
      start.current = false;
      bestScore.current = 0;
      initializeBestUser();
      counter.current = 0;
    } catch (err) {
      throw new Error('restart error');
    }
  }

  async function updateBestScore(currScore) {
    try {
      if (currScore > bestScore.current) {
        bestScore.current = currScore;
        await Storage.updateBest(name.current, currScore);
      }
    } catch (err) {
      throw new Error('update best score error');
    }
  }

  async function QuestionCounter() {
    try {
      counter.current += 1;
      if (counter.current >= 10) {
        updateBestScore(score);
        const best = await Storage.getBestUser();
        bestUser.current = best.user;
        bestOverall.current = best.best;
        topPlayers.current = await Storage.getTopUsers();
        setNext('results');
      } else {
        setNext('question');
      }
    } catch (err) {
      throw new Error('count question error');
    }
  }

  async function handleDelete() {
    try {
      Storage.deleteUser(name.current);
      // setRestart(true);
      RestartGame();
    } catch (err) {
      throw new Error('delete error');
    }
  }

  if (!start.current) {
    return (
      <View>
        <View style={styles.title}>
          <View style={styles.blueRectangle}>
            <Text style={styles.titleText}>Guess the Celebrity</Text>
          </View>
        </View>
        <View style={styles.loginBox}>
          <View style={styles.cyanRectangle}>
            <View style={styles.whiteRectangle}>
              <Login LoginUser={LoginUser} error={error} />
            </View>
          </View>
        </View>
      </View>
    );
  }

  // haven't reached 10 questions yet
  if (nextPage === 'question') {
    return (
      <View>
        <View style={styles.infoDeleteContainer}>
          <View style={styles.deleteButtonContainer}>
            <Button title="Delete Account" onPress={handleDelete} />
          </View>
          <View style={styles.infoContainer}>
            <Text style={styles.boldInfo}>
              User:&nbsp;
              <Text style={styles.info}>
                { name.current }
                {' '}
                {'\n'}
              </Text>
            </Text>
            <Text style={styles.boldInfo}>
              Current Score:&nbsp;
              <Text style={styles.info}>
                { score}
                {' '}
                {'\n'}
              </Text>
            </Text>
            <Text style={styles.boldInfo}>
              Best Score:&nbsp;
              <Text style={styles.info}>
                { bestScore.current }
                {' '}
                {'\n'}
              </Text>
            </Text>
            <Text style={styles.boldInfo}>
              Game Leader:&nbsp;
              <Text style={styles.info}>
                { bestUser.current }
                (
                { bestOverall.current }
                )
                {' '}
                {'\n'}
              </Text>
            </Text>
          </View>
        </View>
        <View>
          <Questions
            questionslist={questionsList.current}
            score={score}
            setScore={setScore}
            setCorrect={setCorrect}
            setNext={setNext}
          />
        </View>
      </View>
    );
  }

  // feedack page
  if (nextPage === 'feedback') {
    return (
      <View>
        <View style={styles.infoDeleteContainer}>
          <View style={styles.deleteButtonContainer}>
            <Button title="Delete Account" onPress={handleDelete} />
          </View>
          <View style={styles.infoContainer}>
            <Text style={styles.boldInfo}>
              User:&nbsp;
              <Text style={styles.info}>
                { name.current }
                {' '}
                {'\n'}
              </Text>
            </Text>
            <Text style={styles.boldInfo}>
              Current Score:&nbsp;
              <Text style={styles.info}>
                { score}
                {' '}
                {'\n'}
              </Text>
            </Text>
            <Text style={styles.boldInfo}>
              Best Score:&nbsp;
              <Text style={styles.info}>
                { bestScore.current }
                {' '}
                {'\n'}
              </Text>
            </Text>
            <Text style={styles.boldInfo}>
              Game Leader:&nbsp;
              <Text style={styles.info}>
                { bestUser.current }
                (
                { bestOverall.current }
                )
                {' '}
                {'\n'}
              </Text>
            </Text>
          </View>
        </View>
        <View>
          <Feedback correct={correct} Counter={QuestionCounter} />
        </View>
      </View>
    );
  }

  // finished 10 questions
  if (nextPage === 'results') {
    return (
      <View>
        <View style={styles.infoDeleteContainer}>
          <View style={styles.deleteButtonContainer}>
            <Button title="Delete Account" onPress={handleDelete} />
          </View>
          <View style={styles.infoContainer}>
            <Text style={styles.boldInfo}>
              User:&nbsp;
              <Text style={styles.info}>
                { name.current }
                {' '}
                {'\n'}
              </Text>
            </Text>
            <Text style={styles.boldInfo}>
              Current Score:&nbsp;
              <Text style={styles.info}>
                { score}
                {' '}
                {'\n'}
              </Text>
            </Text>
            <Text style={styles.boldInfo}>
              Best Score:&nbsp;
              <Text style={styles.info}>
                { bestScore.current }
                {' '}
                {'\n'}
              </Text>
            </Text>
            <Text style={styles.boldInfo}>
              Game Leader:&nbsp;
              <Text style={styles.info}>
                { bestUser.current }
                (
                { bestOverall.current }
                )
                {' '}
                {'\n'}
              </Text>
            </Text>
          </View>
        </View>
        <View>
          <Results Score={score} HandleRestart={RestartGame} TopPlayers={topPlayers.current} />
        </View>
      </View>
    );
  }
}

export default App;
