/* eslint-disable react/prop-types */
/* eslint-disable react/jsx-filename-extension */
import { React, useState } from 'react';
// import '../assets/Login.css';
import {
  StyleSheet, Text, View, TextInput, Button,
} from 'react-native';

function Login({ LoginUser, error }) {
  const styles = StyleSheet.create({
    input: {
      height: 24,
      width: 160,
      marginTop: 20,
      borderWidth: 1,
      padding: 8,
      borderRadius: 20,
    },
    loginText: {
      textAlign: 'center',
      fontSize: 24,
    },
    usernameText: {
      textAlign: 'center',
      marginTop: 20,
      fontSize: 18,
    },
    errorText: {
      textAlign: 'center',
      marginTop: 6,
    },
    goButton: {
      marginTop: 20,
    },
  });

  const [info, setInfo] = useState({ name: '' });

  const changeInfo = (e) => {
    setInfo({ name: e });
  };
  const handleLogin = (e) => {
    e.preventDefault();

    LoginUser(info);
  };

  return (
    <View>
      <View>
        {(error !== '') ? (<View><Text style={styles.errorText}>{error}</Text></View>) : <View><Text style={styles.errorText} /></View>}
        <Text style={styles.loginText}>Login</Text>
        <View>
          <Text style={styles.usernameText}>Username</Text>
          <TextInput
            style={styles.input}
            onChangeText={(e) => changeInfo(e)}
            placeholder="enter here"
          />
        </View>
        <View style={styles.goButton}>
          <Button title="GO!" onPress={handleLogin} />
        </View>
      </View>
    </View>
  );
}

export default Login;
