/* eslint-disable react/prop-types */
/* eslint-disable react/jsx-filename-extension */
import { React } from 'react';
import {
  StyleSheet, Text, View, Button,
} from 'react-native';

function Feedback({ correct, Counter }) {
  const styles = StyleSheet.create({
    lightBlueRectangle: {
      backgroundColor: 'lightblue',
      padding: 10,
      marginBottom: 20,
      marginTop: 40,
      borderRadius: 10,
      width: 240,
      height: 160,
      textAlign: 'center',
      color: 'white',
      fontWeight: '700',
      fontSize: 40,
      alignItems: 'center',
      justifyContent: 'center',
    },
    lightRedRectangle: {
      backgroundColor: 'rgb(255, 142, 142)',
      padding: 10,
      marginBottom: 20,
      marginTop: 40,
      borderRadius: 10,
      width: 240,
      height: 160,
      textAlign: 'center',
      color: 'white',
      fontWeight: '700',
      fontSize: 40,
      alignItems: 'center',
      justifyContent: 'center',
    },
    boxContainer: {
      alignItems: 'center',
    },
    feedbackText: {
      color: 'white',
      fontSize: 32,
      fontWeight: '700',
    },
  });

  let color = styles.lightBlueRectangle;
  if (!correct) {
    color = styles.lightRedRectangle;
  }

  return (
    <View>
      <View>
        <View>
          <View style={styles.boxContainer}>
            <View style={color}>
              <Text style={styles.feedbackText}>{correct ? 'Correct!' : 'Incorrect!'}</Text>
            </View>
          </View>
        </View>
        <View style={{ marginTop: 20 }}>
          <Button title="Next" onPress={Counter} />
        </View>
      </View>
    </View>
  );
}

export default Feedback;
